WITH CTE AS (
    SELECT *,
           ROW_NUMBER() OVER(PARTITION BY {pipeline().parameters.campos} ORDER BY (SELECT NULL)) AS rn
    FROM {pipeline().parameters.tabla}
)
DELETE FROM CTE WHERE rn > 1;

